mapper.insert:
INSERT INTO FILE(
	ATCH_FILE_ID
	, FILE_SN
	, CREAT_DT
	, USE_AT
	, FILE_STRE_COURS
	, STRE_FILE_NM
	, ORIGNL_FILE_NM
	, FILE_EXTSN
	, FILE_SIZE
) VALUES(
	#{atchFileId}
	, #{fileSn}
	, NOW()
	, #{useAt}
	, #{fileStreCours}
	, #{streFileNm}
	, #{orignlFileNm}
	, #{fileExtsn}
	, #{fileSize}
)

mapper.update:
UPDATE FILE SET 
	USE_AT = #{useAt}

	, DELETED_AT = NOW()

WHERE 
	ATCH_FILE_ID = #{atchFileId}
		AND FILE_SN = #{fileSn}
