mapper.select:
SELECT A.BOARD_ID , A.CATEGORY , A.BOARD_SJ , A.BOARD_CN , A.INQIRE_CO , A.RECOMMEND_CNT , A.CREAT_IP , A.IS_NOTICE , A.OTHBC_AT , A.USE_AT , A.ATCH_FILE_ID , A.FRST_REGIST_PNTTM , A.REGISTER_ID , B.NICKNAME FROM BOARD A JOIN USERINFO B ON A.REGISTER_ID = B.USER_ID
ORDER BY A.FRST_REGIST_PNTTM DESC
LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

mapper.update:
UPDATE BOARD SET 
		INQIRE_CO = INQIRE_CO+1 
WHERE BOARD_ID = #{boardId};

mapper.insert:
INSERT INTO BOARD (
	BOARD_ID
	, CATEGORY
	, BOARD_SJ
	, BOARD_CN
	, INQIRE_CO
	, CREAT_IP
	, OTHBC_AT
	, USE_AT
	, ATCH_FILE_ID
	, FRST_REGIST_PNTTM
	, LAST_UPDT_PNTTM
	, REGISTER_ID
	, RECOMMEND_CNT
	, IS_NOTICE
) VALUES(
	#{boardId}
	, #{category}
	, #{boardSj}
	, #{boardCn}
	, 0
	, #{creatIp}
	, #{othbcAt}
	, 'Y'
	, NULL
	, NOW()
	, NOW()
	, #{registerId}
	, 0
	, #{isNotice}
)
