mapper.update:
UPDATE BOARD SET 
		INQIRE_CO = INQIRE_CO+1 
WHERE BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO BOARD (
	BOARD_ID
	, CATEGORY
	, BOARD_SJ
	, BOARD_CN
	, INQIRE_CO
	, CREAT_IP
	, OTHBC_AT
	, USE_AT
	, ATCH_FILE_ID
	, FRST_REGIST_PNTTM
	, LAST_UPDT_PNTTM
	, REGISTER_ID
	, RECOMMEND_CNT
	, IS_NOTICE
) VALUES(
	#{boardId}
	, #{category}
	, #{boardSj}
	, #{boardCn}
	, 0
	, #{creatIp}
	, #{othbcAt}
	, 'Y'
	, #{atchFileId}
	, NOW()
	, NOW()
	, #{registerId}
	, 0
	, #{isNotice}
)

mapper.update:
UPDATE BOARD SET
	CATEGORY = #{category}
	, BOARD_SJ = #{boardSj}
	, BOARD_CN  = #{boardCn}
	, ATCH_FILE_ID = #{atchFileId}

	, IS_NOTICE  = #{isNotice}


	, OTHBC_AT  = #{othbcAt}

	, LAST_UPDT_PNTTM  = NOW()
WHERE 
	BOARD_ID = #{boardId}

		AND REGISTER_ID = #{userId}

UPDATE BOARD 
SET USE_AT = 'N'
WHERE 
	BOARD_ID = #{boardId}

	AND REGISTER_ID = #{userId}

UPDATE BOARD SET 
		RECOMMEND_CNT = RECOMMEND_CNT+1 
WHERE BOARD_ID = #{boardId}

mapper.insert:
INSERT INTO RECOMMEND (
	USER_ID 
	, BOARD_ID 
	, NUM_RECOMMENDATIONS 
) VALUES (
	#{userId}
	, #{boardId}
	, 1
)

mapper.update:
UPDATE RECOMMEND SET 
	NUM_RECOMMENDATIONS = NUM_RECOMMENDATIONS+1
WHERE BOARD_ID = #{boardId}
	AND USER_ID = #{userId}
